mapper.select:
    select

    from sys_menu
    where 1=1

        and menu_id = #{menuId,jdbcType=BIGINT}


        and pid = #{pid,jdbcType=BIGINT}


        and sub_count = #{subCount,jdbcType=INTEGER}


        and type = #{type,jdbcType=INTEGER}


        and title = #{title,jdbcType=VARCHAR}


        and name = #{name,jdbcType=VARCHAR}


        and component = #{component,jdbcType=VARCHAR}


        and menu_sort = #{menuSort,jdbcType=INTEGER}


        and icon = #{icon,jdbcType=VARCHAR}


        and path = #{path,jdbcType=VARCHAR}


        and i_frame = #{iFrame,jdbcType=BIT}


        and cache = #{cache,jdbcType=BIT}


        and hidden = #{hidden,jdbcType=BIT}


        and permission = #{permission,jdbcType=VARCHAR}


        and create_by = #{createBy,jdbcType=VARCHAR}


        and update_by = #{updateBy,jdbcType=VARCHAR}

AND create_time >= #{beginTime}
AND create_time <=#{afterTIme}
AND update_time >= #{beginTime}
AND update_time <=#{afterTIme}

