mapper.select:
select

from sys_dict
where 1=1

            and dict_id = #{dictId,jdbcType=BIGINT}


            and name = #{name,jdbcType=VARCHAR}


            and description = #{description,jdbcType=VARCHAR}


            and create_by = #{createBy,jdbcType=VARCHAR}


            and update_by = #{updateBy,jdbcType=VARCHAR}


            and create_time = #{createTime,jdbcType=TIMESTAMP}


            and update_time = #{updateTime,jdbcType=TIMESTAMP}

limit 1

select

from sys_dict
where 1=1

            and dict_id = #{record.dictId,jdbcType=BIGINT}


            and name = #{record.name,jdbcType=VARCHAR}


            and description = #{record.description,jdbcType=VARCHAR}


            and create_by = #{record.createBy,jdbcType=VARCHAR}


            and update_by = #{record.updateBy,jdbcType=VARCHAR}


            and create_time = #{record.createTime,jdbcType=TIMESTAMP}


            and update_time = #{record.updateTime,jdbcType=TIMESTAMP}

mapper.update:
       update sys_dict

dict_id =#{dictId,jdbcType=BIGINT}, name =#{name,jdbcType=VARCHAR}, description =#{description,jdbcType=VARCHAR}, create_by =#{createBy,jdbcType=VARCHAR}, update_by =#{updateBy,jdbcType=VARCHAR}, create_time =#{createTime,jdbcType=TIMESTAMP}, update_time =#{updateTime,jdbcType=TIMESTAMP},        
       where dict_id = #{dictId,jdbcType=BIGINT}
