mapper.select:
    select

    from sys_user
    where 1=1

        and user_id = #{userId,jdbcType=BIGINT}


        and username = #{username,jdbcType=VARCHAR}


        and nick_name = #{nickName,jdbcType=VARCHAR}


        and gender = #{gender,jdbcType=VARCHAR}


        and phone = #{phone,jdbcType=VARCHAR}


        and email = #{email,jdbcType=VARCHAR}


        and region = #{region,jdbcType=VARCHAR}


        and service_life = #{serviceLife,jdbcType=VARCHAR}


        and avatar_name = #{avatarName,jdbcType=VARCHAR}


        and avatar_path = #{avatarPath,jdbcType=VARCHAR}


        and star_rating = #{starRating,jdbcType=VARCHAR}


        and password = #{password,jdbcType=VARCHAR}


        and enabled = #{enabled,jdbcType=INTEGER}


        and create_by = #{createBy,jdbcType=VARCHAR}


        and update_by = #{updateBy,jdbcType=VARCHAR}


        and pwd_reset_time = #{pwdResetTime,jdbcType=TIMESTAMP}


        and create_time = #{createTime,jdbcType=TIMESTAMP}

AND update_time >= #{beginTime}
AND update_time <=#{afterTIme}

